// Prepare parameters for email fetching with comprehensive debugging
const input = $input.all()[0]?.json || {};

// Default to today's emails if no specific timeframe provided
const hoursBack = input.hoursBack || 24;
const maxEmails = input.maxEmails || 20;
const onlyUnread = input.onlyUnread !== false; // default true

// Calculate date filter (Gmail uses YYYY/MM/DD format)
const cutoffDate = new Date();
cutoffDate.setHours(cutoffDate.getHours() - hoursBack);
const dateFilter = cutoffDate.toISOString().split('T')[0].replace(/-/g, '/');

// Build Gmail search query
let query = `after:${dateFilter}`;
if (onlyUnread) {
  query += ' is:unread';
}
// Exclude automated emails
query += ' -from:noreply -from:no-reply -from:donotreply';

console.log('Email Query Prepared:', {
  hoursBack,
  maxEmails,
  onlyUnread,
  dateFilter,
  query
});

return [{
  json: {
    query: query,
    maxEmails: maxEmails,
    hoursBack: hoursBack,
    dateFilter: dateFilter,
    onlyUnread: onlyUnread,
    debug: {
      step: 'query-preparation',
      timestamp: new Date().toISOString(),
      query: query
    }
  }
}];

// Process and clean email data for AI summarization with comprehensive debugging
const items = $input.all();

console.log('Process Email Data - Input received:', {
  itemCount: items?.length || 0,
  hasItems: !!items,
  firstItemExists: !!(items && items[0]),
  firstItemHasJson: !!(items && items[0] && items[0].json)
});

if (!items || items.length === 0) {
  console.log('No input items received');
  return [{
    json: {
      emailCount: 0,
      summary: "No input data received from Gmail fetch.",
      ok: false,
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      needsSummary: false,
      debug: {
        step: 'process-emails',
        issue: 'no-input-items',
        itemCount: 0
      }
    }
  }];
}

if (!items[0] || !items[0].json) {
  console.log('First item missing or has no JSON data');
  return [{
    json: {
      emailCount: 0,
      summary: "Invalid data structure from Gmail fetch.",
      ok: false,
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      needsSummary: false,
      debug: {
        step: 'process-emails',
        issue: 'invalid-data-structure',
        hasFirstItem: !!items[0],
        hasJson: !!(items[0] && items[0].json)
      }
    }
  }];
}

// Check if Gmail returned an error
if (items[0].json.error) {
  console.log('Gmail API returned an error:', items[0].json.error);
  return [{
    json: {
      emailCount: 0,
      summary: `Gmail API error: ${items[0].json.error}`,
      ok: false,
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      needsSummary: false,
      debug: {
        step: 'process-emails',
        issue: 'gmail-api-error',
        error: items[0].json.error
      }
    }
  }];
}

const emails = items.map(item => item.json);
console.log(`Raw emails fetched: ${emails.length}`);

// Helper function to extract plain text from email body
function extractTextFromEmail(email) {
  let text = '';

  if (email.payload) {
    // Handle multipart emails
    if (email.payload.parts) {
      for (const part of email.payload.parts) {
        if (part.mimeType === 'text/plain' && part.body.data) {
          text += Buffer.from(part.body.data, 'base64').toString('utf-8');
        }
      }
    }
    // Handle single part emails
    else if (email.payload.mimeType === 'text/plain' && email.payload.body.data) {
      text = Buffer.from(email.payload.body.data, 'base64').toString('utf-8');
    }
  }

  // Clean up the text
  return text
    .replace(/\r\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .trim()
    .substring(0, 1000); // Limit to 1000 chars per email
}

// Helper function to get header value
function getHeader(email, name) {
  if (!email.payload || !email.payload.headers) return '';
  const header = email.payload.headers.find(h => h.name.toLowerCase() === name.toLowerCase());
  return header ? header.value : '';
}

// Process emails for summarization
const processedEmails = emails.map((email, index) => {
  const processed = {
    from: getHeader(email, 'From'),
    subject: getHeader(email, 'Subject'),
    date: getHeader(email, 'Date'),
    snippet: email.snippet || '',
    bodyText: extractTextFromEmail(email)
  };

  console.log(`Email ${index + 1}:`, {
    from: processed.from,
    subject: processed.subject,
    hasDate: !!processed.date,
    hasSnippet: !!processed.snippet,
    bodyLength: processed.bodyText.length
  });

  return processed;
}).filter(email => {
  const hasBasicInfo = email.from && email.subject;

  // More lenient filtering - only exclude obvious automated emails
  const isNotAutomated = !email.from.toLowerCase().includes('donotreply') && 
                         !email.from.toLowerCase().includes('do-not-reply') &&
                         !email.subject.toLowerCase().includes('unsubscribe') &&
                         !email.subject.toLowerCase().includes('automated');

  // Allow noreply emails through if they have meaningful content
  const passes = hasBasicInfo && isNotAutomated;

  if (!passes) {
    console.log('Email filtered out:', {
      from: email.from,
      subject: email.subject,
      hasBasicInfo,
      isNotAutomated,
      reason: !hasBasicInfo ? 'missing basic info' : 'automated email'
    });
  } else {
    console.log('Email accepted:', {
      from: email.from,
      subject: email.subject
    });
  }

  return passes;
});

console.log(`Processed emails after filtering: ${processedEmails.length}`);

if (processedEmails.length === 0) {
  console.log('No emails passed filtering');
  return [{
    json: {
      emailCount: 0,
      summary: "No relevant emails found to summarize (all emails were filtered out).",
      ok: true,
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      needsSummary: false,
      debug: {
        step: 'process-emails',
        issue: 'all-emails-filtered',
        rawEmailCount: emails.length,
        processedEmailCount: 0,
        reason: "All emails were either missing basic info or from noreply addresses"
      }
    }
  }];
}

// Prepare data for Gemini API call
const emailsForAI = processedEmails.slice(0, 10); // Limit to 10 emails max

console.log('Successfully processed emails for AI:', {
  finalCount: emailsForAI.length,
  needsSummary: true
});

return [{
  json: {
    emailCount: emailsForAI.length,
    emails: emailsForAI,
    needsSummary: true,
    debug: {
      step: 'process-emails',
      rawEmailCount: emails.length,
      processedEmailCount: processedEmails.length,
      finalEmailCount: emailsForAI.length,
      timestamp: new Date().toISOString()
    }
  }
}];

// Prepare Gemini API request with comprehensive debugging
const inputItems = $input.all();

console.log('Prepare Gemini Request - Input received:', {
  itemCount: inputItems?.length || 0,
  hasItems: !!inputItems,
  firstItemExists: !!(inputItems && inputItems[0]),
  firstItemHasJson: !!(inputItems && inputItems[0] && inputItems[0].json)
});

if (!inputItems || inputItems.length === 0) {
  console.log('No input items received in Prepare Gemini Request');
  return [{
    json: {
      ok: false,
      error: "No input data received in Prepare Gemini Request",
      code: "NO_INPUT_DATA",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      debug: {
        step: 'prepare-gemini-request',
        issue: 'no-input-items'
      }
    }
  }];
}

const data = inputItems[0].json;

console.log('Data received in Prepare Gemini Request:', {
  hasData: !!data,
  hasEmails: !!(data && data.emails),
  emailCount: data?.emails?.length || 0,
  dataKeys: data ? Object.keys(data) : 'no data',
  needsSummary: data?.needsSummary
});

// Add a safeguard in case this node is reached without emails
if (!data || !data.emails || data.emails.length === 0) {
  console.log('No emails found in data');
  return [{
    json: {
      ok: false,
      error: "Attempted to summarize with no emails.",
      code: "LOGIC_ERROR",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      debug: {
        step: 'prepare-gemini-request',
        issue: 'no-emails',
        hasData: !!data,
        hasEmails: !!(data && data.emails),
        emailCount: data?.emails?.length || 0
      }
    }
  }];
}

const emails = data.emails;
console.log(`Building context for ${emails.length} emails`);

// Build context for Gemini
const emailContext = emails.map((email, i) => 
  `EMAIL ${i + 1}:
` +
  `From: ${email.from}
` +
  `Subject: ${email.subject}
` +
  `Date: ${email.date}
` +
  `Content: ${email.bodyText || email.snippet}
` +
  `---`
).join('\n\n');

const prompt = `You are an AI assistant helping summarize recent emails. Please analyze these ${emails.length} emails and create a concise, organized summary.

${emailContext}

Please provide a summary in this format:

# Email Summary (${emails.length} emails)

## 🔥 Urgent/Important
[List any urgent emails requiring immediate attention]

## 📅 Meetings & Events
[List any meeting invitations, calendar events, or scheduling]

## 💼 Work Updates
[List work-related updates, project communications, etc.]

## 📧 Other Communications
[List other notable emails]

## 📊 Summary Stats
- Total emails: ${emails.length}
- Time period: Last 24 hours
- Unread count: [count if available]

Keep it concise but informative. Focus on actionable items and important communications.`;

const geminiPayload = {
  contents: [{
    parts: [{
      text: prompt
    }]
  }],
  generationConfig: {
    temperature: 0.3,
    topK: 40,
    topP: 0.95,
    maxOutputTokens: 1000
  }
};

console.log('Gemini payload prepared successfully:', {
  emailCount: emails.length,
  promptLength: prompt.length,
  hasPayload: !!geminiPayload
});

return [{
  json: {
    geminiPayload: geminiPayload,
    emailCount: emails.length,
    originalData: data,
    debug: {
      step: 'prepare-gemini-request',
      emailCount: emails.length,
      promptLength: prompt.length,
      timestamp: new Date().toISOString()
    }
  }
}];

// Call Gemini API with comprehensive debugging and error handling
const inputItems = $input.all();

console.log('Call Gemini API - Input received:', {
  itemCount: inputItems?.length || 0,
  hasItems: !!inputItems,
  firstItemExists: !!(inputItems && inputItems[0]),
  firstItemHasJson: !!(inputItems && inputItems[0] && inputItems[0].json)
});

// Debug: Check if we have input data
if (!inputItems || inputItems.length === 0) {
  console.log('No input data received in Call Gemini API');
  return [{
    json: {
      ok: false,
      error: "No input data received",
      code: "NO_INPUT_DATA",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      debug: { 
        step: 'call-gemini-api',
        issue: 'no-input-data',
        inputLength: inputItems ? inputItems.length : 'undefined' 
      }
    }
  }];
}

const data = inputItems[0].json;

console.log('Data received in Call Gemini API:', {
  hasData: !!data,
  hasGeminiPayload: !!(data && data.geminiPayload),
  dataKeys: data ? Object.keys(data) : 'no data',
  emailCount: data?.emailCount
});

// Safeguard in case this node is reached without proper data
if (!data || !data.geminiPayload) {
  console.log('No Gemini payload found in Call Gemini API');
  return [{
    json: {
      ok: false,
      error: "No Gemini payload found",
      code: "LOGIC_ERROR",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      debug: { 
        step: 'call-gemini-api',
        issue: 'no-gemini-payload',
        hasData: !!data,
        hasGeminiPayload: !!(data && data.geminiPayload),
        dataKeys: data ? Object.keys(data) : 'no data'
      }
    }
  }];
}

const geminiPayload = data.geminiPayload;
const apiKey = "YOUR_API_KEY_HERE"; // Replace with actual API key
const url = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash-exp:generateContent?key=${apiKey}`;

console.log('Making Gemini API call:', {
  url: url.replace(apiKey, 'HIDDEN_API_KEY'),
  hasPayload: !!geminiPayload,
  payloadSize: JSON.stringify(geminiPayload).length
});

// Check if API key is still placeholder
if (apiKey === "YOUR_API_KEY_HERE") {
  console.log('API key is still placeholder');
  return [{
    json: {
      ok: false,
      error: "API key not configured",
      code: "CONFIG_ERROR",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      debug: {
        step: 'call-gemini-api',
        issue: 'api-key-not-configured',
        message: 'Please replace YOUR_API_KEY_HERE with actual Gemini API key'
      }
    }
  }];
}

try {
  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(geminiPayload)
  });

  console.log('Gemini API response received:', {
    status: response.status,
    statusText: response.statusText,
    ok: response.ok
  });

  if (!response.ok) {
    const errorText = await response.text();
    console.log('Gemini API error response:', errorText);
    throw new Error(`HTTP error! status: ${response.status}, message: ${errorText}`);
  }

  const responseData = await response.json();

  console.log('Gemini API success:', {
    hasCandidates: !!(responseData.candidates && responseData.candidates.length > 0),
    candidateCount: responseData.candidates?.length || 0
  });

  return [{
    json: {
      ...responseData,
      debug: {
        step: 'call-gemini-api',
        success: true,
        timestamp: new Date().toISOString(),
        emailCount: data.emailCount
      }
    }
  }];
} catch (error) {
  console.error('Gemini API error:', error);
  return [{
    json: {
      ok: false,
      error: "Failed to generate AI summary",
      code: "GEMINI_API_ERROR",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      fallbackSummary: `Found ${data.emailCount || 0} recent emails. AI summarization temporarily unavailable.`,
      errorDetails: error.message,
      debug: {
        step: 'call-gemini-api',
        issue: 'api-call-failed',
        errorMessage: error.message,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Process Gemini response and format final output
const inputItems = $input.all();

// Debug: Check if we have input data
if (!inputItems || inputItems.length === 0) {
  return [{
    json: {
      ok: false,
      error: "No input data received in format response",
      code: "NO_INPUT_DATA",
      brick: "summarize_emails",
      timestamp: new Date().toISOString()
    }
  }];
}

const geminiResponse = inputItems[0].json;

// Check if this is an error response from the Gemini API call
if (!geminiResponse) {
  return [{
    json: {
      ok: false,
      error: "No response data from Gemini API",
      code: "EMPTY_RESPONSE",
      brick: "summarize_emails",
      timestamp: new Date().toISOString()
    }
  }];
}

// If it's already an error response, pass it through
if (geminiResponse.ok === false) {
  return [{ json: geminiResponse }];
}

try {
  // Extract the summary from Gemini response
  let summary = "Unable to generate summary.";

  if (geminiResponse.candidates && 
      geminiResponse.candidates[0] && 
      geminiResponse.candidates[0].content && 
      geminiResponse.candidates[0].content.parts && 
      geminiResponse.candidates[0].content.parts[0]) {

    summary = geminiResponse.candidates[0].content.parts[0].text;
  }

  // Clean up the summary
  summary = summary
    .replace(/\*\*/g, '**') // Ensure markdown formatting
    .replace(/\n{3,}/g, '\n\n') // Clean excessive newlines
    .trim();

  return [{
    json: {
      ok: true,
      summary: summary,
      emailCount: geminiResponse.emailCount || 'unknown',
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      metadata: {
        generatedBy: "gemini-2.0-flash-exp",
        processingTime: new Date().toISOString()
      }
    }
  }];

} catch (error) {
  // Handle Gemini API errors gracefully
  console.error('Gemini API error:', error);

  return [{
    json: {
      ok: false,
      error: "Failed to process AI summary",
      code: "PROCESSING_ERROR",
      brick: "summarize_emails",
      timestamp: new Date().toISOString(),
      fallbackSummary: "AI summarization temporarily unavailable.",
      errorDetails: error.message
    }
  }];
}
